import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaUser, FaEnvelope, FaMoneyBill, FaClock } from 'react-icons/fa';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE,
});

const Books = () => {
  const [books, setBooks] = useState([]);

  const fetchBooks = async () => {
    try {
      const res = await API.get('/api/books');
      setBooks(res.data);
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };

  useEffect(() => {
    fetchBooks();
  }, []);

  return (
    <div>
      <h2>Books for Sale</h2>
      {books.length === 0 ? (
        <p>No books available.</p>
      ) : (
        <ul>
          {books.map((book) => (
            <li key={book._id} style={{ border: '1px solid gray', marginBottom: '10px', padding: '10px' }}>
              <img
                src={`${process.env.REACT_APP_API_BASE}${book.image}`}
                alt={book.name}
                style={{ width: '150px', height: 'auto', marginBottom: '10px' }}
                onError={(e) => {
                  e.target.onerror = null;
                  e.target.src = 'https://via.placeholder.com/150';
                }}
              />
              <h3>{book.name}</h3>
              <p><FaUser /> Seller: {book.seller}</p>
              <p><FaEnvelope /> Email: {book.email}</p>
              <p><FaMoneyBill /> Price: â‚¹{book.price}</p>
              <p><FaClock /> Uploaded: {new Date(book.createdAt).toLocaleDateString()}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Books;
