import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaEdit, FaTrash } from 'react-icons/fa';

const BASE_URL = 'http://localhost:5000'; // Update this if your backend runs elsewhere

const AdminDepartments = () => {
  const [universities, setUniversities] = useState([]);
  const [selectedUniversityId, setSelectedUniversityId] = useState('');
  const [departments, setDepartments] = useState([]);
  const [editingIndex, setEditingIndex] = useState(null);
  const [newName, setNewName] = useState('');

  // Fetch universities on mount
  useEffect(() => {
    const fetchUniversities = async () => {
      try {
        const res = await axios.get(`${BASE_URL}/api/universities`);
        setUniversities(res.data);
      } catch (err) {
        console.error('Error fetching universities:', err);
      }
    };
    fetchUniversities();
  }, []);

  // Update department list when university changes
  useEffect(() => {
    if (!selectedUniversityId) {
      setDepartments([]);
      return;
    }

    const selectedUni = universities.find((u) => u._id === selectedUniversityId);
    if (selectedUni) {
      setDepartments(selectedUni.departments || []);
    }
  }, [selectedUniversityId, universities]);

  const handleEdit = (index) => {
    setEditingIndex(index);
    setNewName(departments[index].name);
  };

  const handleUpdate = async (deptId) => {
    try {
      await axios.put(`${BASE_URL}/api/universities/${selectedUniversityId}/departments/${deptId}`, {
        name: newName,
      });

      const updated = [...departments];
      updated[editingIndex].name = newName;
      setDepartments(updated);
      setEditingIndex(null);
      alert('✅ Department updated successfully');
    } catch (err) {
      console.error('Update failed:', err);
      alert('❌ Failed to update department');
    }
  };

  const handleDelete = async (deptId) => {
    try {
      await axios.delete(`${BASE_URL}/api/universities/${selectedUniversityId}/departments/${deptId}`);
      const updatedDepartments = departments.filter((d) => d._id !== deptId);
      setDepartments(updatedDepartments);
      alert('✅ Department deleted successfully');
    } catch (err) {
      console.error('Delete failed:', err);
      alert('❌ Failed to delete department');
    }
  };

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h2 className="text-xl font-bold mb-4">Manage Departments</h2>

      {/* University dropdown */}
      <select
        value={selectedUniversityId}
        onChange={(e) => setSelectedUniversityId(e.target.value)}
        className="border p-2 mb-4 w-full"
      >
        <option value="">-- Select University --</option>
        {universities.map((u) => (
          <option key={u._id} value={u._id}>
            {u.name}
          </option>
        ))}
      </select>

      {/* Departments list */}
      {departments.length === 0 && selectedUniversityId && (
        <p className="text-gray-500">No departments found.</p>
      )}

      {departments.map((dept, index) => (
        <div
          key={dept._id}
          className="flex items-center justify-between bg-white shadow-md p-3 mb-2 rounded"
        >
          {editingIndex === index ? (
            <input
              type="text"
              value={newName}
              onChange={(e) => setNewName(e.target.value)}
              className="border p-1 flex-grow mr-2"
            />
          ) : (
            <span>{dept.name}</span>
          )}
          <div className="space-x-2">
            {editingIndex === index ? (
              <button
                onClick={() => handleUpdate(dept._id)}
                className="bg-green-500 text-white px-2 py-1 rounded"
              >
                Save
              </button>
            ) : (
              <button
                onClick={() => handleEdit(index)}
                className="text-blue-600 hover:text-blue-800"
              >
                <FaEdit />
              </button>
            )}
            <button
              onClick={() => handleDelete(dept._id)}
              className="text-red-600 hover:text-red-800"
            >
              <FaTrash />
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default AdminDepartments;
