import React, { useState } from 'react';

const Syllabus = () => {
  const [syllabusList, setSyllabusList] = useState([
    {
      university: 'Sardar Patel University, Mandi',
      course: 'B.Sc CS',
      semester: '1st',
      subject: 'Programming Fundamentals',
      pdf: 'syllabus-pf.pdf'
    },
    {
      university: 'IIT Mandi',
      course: 'B.Tech CSE',
      semester: '3rd',
      subject: 'Algorithms',
      pdf: 'syllabus-algorithms.pdf'
    }
  ]);

  const [showForm, setShowForm] = useState(false);
  const [newSyllabus, setNewSyllabus] = useState({
    university: '',
    course: '',
    semester: '',
    subject: '',
    pdf: ''
  });

  const handleDelete = (index) => {
    const updated = syllabusList.filter((_, i) => i !== index);
    setSyllabusList(updated);
  };

  const handleAdd = () => {
    if (newSyllabus.university && newSyllabus.course && newSyllabus.semester && newSyllabus.subject && newSyllabus.pdf) {
      setSyllabusList([...syllabusList, newSyllabus]);
      setNewSyllabus({ university: '', course: '', semester: '', subject: '', pdf: '' });
      setShowForm(false);
    } else {
      alert('Please fill in all fields.');
    }
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setNewSyllabus({ ...newSyllabus, pdf: file.name });
    }
  };

  return (
    <div className="p-6 bg-white rounded shadow">
      <h2 className="text-2xl font-bold mb-4">Manage Syllabus</h2>

      <button
        onClick={() => setShowForm(!showForm)}
        className="mb-4 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
      >
        {showForm ? 'Cancel' : 'Add Syllabus'}
      </button>

      {showForm && (
        <div className="mb-6 bg-gray-100 p-4 rounded shadow space-y-3">
          <input
            type="text"
            placeholder="University"
            value={newSyllabus.university}
            onChange={(e) => setNewSyllabus({ ...newSyllabus, university: e.target.value })}
            className="w-full border px-3 py-2 rounded"
          />
          <input
            type="text"
            placeholder="Course"
            value={newSyllabus.course}
            onChange={(e) => setNewSyllabus({ ...newSyllabus, course: e.target.value })}
            className="w-full border px-3 py-2 rounded"
          />
          <input
            type="text"
            placeholder="Semester"
            value={newSyllabus.semester}
            onChange={(e) => setNewSyllabus({ ...newSyllabus, semester: e.target.value })}
            className="w-full border px-3 py-2 rounded"
          />
          <input
            type="text"
            placeholder="Subject"
            value={newSyllabus.subject}
            onChange={(e) => setNewSyllabus({ ...newSyllabus, subject: e.target.value })}
            className="w-full border px-3 py-2 rounded"
          />
          <div>
            <label className="block mb-1 font-medium">Upload PDF</label>
            <label className="bg-blue-600 text-white px-4 py-2 rounded cursor-pointer hover:bg-blue-700 inline-block">
              Choose File
              <input
                type="file"
                accept="application/pdf"
                onChange={handleFileChange}
                className="hidden"
              />
            </label>
            {newSyllabus.pdf && <span className="ml-2 text-sm text-gray-700">{newSyllabus.pdf}</span>}
          </div>
          <button
            onClick={handleAdd}
            className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
          >
            Submit
          </button>
        </div>
      )}

      <div className="space-y-4">
        {syllabusList.map((syllabus, index) => (
          <div key={index} className="border p-4 rounded shadow-sm">
            <h3 className="text-lg font-semibold text-blue-700">{syllabus.subject} ({syllabus.semester})</h3>
            <p className="text-sm text-gray-600">{syllabus.course} - {syllabus.university}</p>
            <a
              href={syllabus.pdf}
              target="_blank"
              rel="noopener noreferrer"
              className="text-blue-500 text-sm hover:underline"
            >
              View PDF
            </a>
            <div className="mt-2">
              <button
                onClick={() => handleDelete(index)}
                className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600 text-sm"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Syllabus;
