import React from 'react';
import { useParams } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBook, faFileAlt, faListAlt } from '@fortawesome/free-solid-svg-icons';

const departmentDetails = {
  'b-tech-cse': {
    years: {
      2022: {
        notes: ['CSE 2022 DSA Notes', 'CSE 2022 DBMS Notes'],
        papers: ['CSE 2022 Midterm', 'CSE 2022 Endterm'],
        syllabus: ['CSE 2022 Semester 1', 'CSE 2022 Semester 2']
      },
      2023: {
        notes: ['CSE 2023 OS Notes', 'CSE 2023 CN Notes'],
        papers: ['CSE 2023 Midterm', 'CSE 2023 Endterm'],
        syllabus: ['CSE 2023 Semester 3', 'CSE 2023 Semester 4']
      }
    }
  },
  'mca': {
    years: {
      2022: {
        notes: ['MCA 2022 Java Notes'],
        papers: ['MCA 2022 Final'],
        syllabus: ['MCA 2022 Full Syllabus']
      },
      2023: {
        notes: ['MCA 2023 Python Notes'],
        papers: ['MCA 2023 Midterm'],
        syllabus: ['MCA 2023 Syllabus Update']
      }
    }
  }
  // Add more programs as needed
};

const DepartmentDetails = () => {
  const { program } = useParams();
  const formattedProgram = program.toLowerCase();
  const details = departmentDetails[formattedProgram];

  if (!details) {
    return <div className="p-8 text-red-600">No data found for this program.</div>;
  }

  return (
    <div className="p-8">
      <h2 className="text-2xl font-bold text-blue-800 mb-6">{program.toUpperCase()} Details</h2>

      {Object.entries(details.years).map(([year, content]) => (
        <div key={year} className="mb-8">
          <h3 className="text-xl font-semibold text-green-700 mb-4">Year: {year}</h3>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div className="bg-white dark:bg-gray-800 p-4 rounded shadow">
              <h4 className="font-semibold text-lg mb-2">
                <FontAwesomeIcon icon={faBook} className="mr-2" />Notes
              </h4>
              <ul>
                {content.notes.map((item, idx) => (
                  <li key={idx} className="mb-1">• {item}</li>
                ))}
              </ul>
            </div>

            <div className="bg-white dark:bg-gray-800 p-4 rounded shadow">
              <h4 className="font-semibold text-lg mb-2">
                <FontAwesomeIcon icon={faFileAlt} className="mr-2" />Question Papers
              </h4>
              <ul>
                {content.papers.map((item, idx) => (
                  <li key={idx} className="mb-1">• {item}</li>
                ))}
              </ul>
            </div>

            <div className="bg-white dark:bg-gray-800 p-4 rounded shadow">
              <h4 className="font-semibold text-lg mb-2">
                <FontAwesomeIcon icon={faListAlt} className="mr-2" />Syllabus
              </h4>
              <ul>
                {content.syllabus.map((item, idx) => (
                  <li key={idx} className="mb-1">• {item}</li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default DepartmentDetails;
