// src/pages/CourseDetailsPage.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const CourseDetailsPage = () => {
  const { universityId, department, courseName } = useParams();
  const [semesterCount, setSemesterCount] = useState(0);
  const [resources, setResources] = useState({});
  const [loading, setLoading] = useState({});

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/universities/${universityId}`)
      .then((res) => {
        const uni = res.data;
        const dept = uni.departments.find((d) => d.name === department);
        const course = dept?.courses.find((c) => c.name === courseName);
        if (course) setSemesterCount(course.semesters);
      })
      .catch((err) => console.error('Error fetching course:', err));
  }, [universityId, department, courseName]);

  const fetchSingleResource = async (semester, type) => {
    const key = `${semester}-${type}`;
    setLoading((prev) => ({ ...prev, [key]: true }));

    try {
      const res = await axios.get(
        `http://localhost:5000/api/resources/${universityId}/${department}/${courseName}/${semester}`
      );
      const filtered = res.data.filter((r) => r.type === type);

      setResources((prev) => ({
        ...prev,
        [semester]: {
          ...prev[semester],
          [type]: filtered,
        },
      }));
    } catch (err) {
      console.error(`Error fetching ${type}`, err);
    }

    setLoading((prev) => ({ ...prev, [key]: false }));
  };

  const renderLinks = (data, type) => {
    if (!data || data.length === 0) return <p className="text-sm">No {type} found.</p>;

    return (
      <ul className="text-sm list-disc ml-4 mt-2">
        {data.map((item, idx) => (
          <li key={idx}>
            <a
              href={`http://localhost:5000/uploads/resources/${item.file}`}
              target="_blank"
              rel="noopener noreferrer"
              className="text-blue-600 underline"
            >
              {item.title} {type === 'papers' && `(${item.year})`}
            </a>
          </li>
        ))}
      </ul>
    );
  };

  return (
    <div className="p-6 min-h-screen bg-gray-50 text-gray-800 dark:bg-gray-900 dark:text-white">
      <h1 className="text-3xl font-bold text-blue-700 mb-6">
        {courseName} â€” {department}
      </h1>

      {semesterCount > 0 ? (
        [...Array(semesterCount)].map((_, i) => {
          const sem = i + 1;
          const semData = resources[sem] || {};

          return (
            <div key={sem} className="mb-8 bg-white dark:bg-gray-800 p-6 rounded-xl shadow border">
              <h2 className="text-2xl font-semibold text-blue-600 mb-4">Semester {sem}</h2>

              {/* Buttons */}
              <div className="flex flex-wrap gap-4 mb-4">
                {['syllabus', 'notes', 'papers'].map((type) => (
                  <div key={type}>
                    <button
                      onClick={() => fetchSingleResource(sem, type)}
                      className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
                    >
                      {loading[`${sem}-${type}`]
                        ? `Loading ${type}...`
                        : `Show ${type.charAt(0).toUpperCase() + type.slice(1)}`}
                    </button>

                    {/* Resource list below button */}
                    {semData[type] && renderLinks(semData[type], type)}
                  </div>
                ))}
              </div>
            </div>
          );
        })
      ) : (
        <p>No semester data found.</p>
      )}
    </div>
  );
};

export default CourseDetailsPage;
