import React, { useState } from 'react';

const Universities = () => {
  const [universities, setUniversities] = useState([
    {
      name: 'Sardar Patel University, Mandi',
      description: 'A state university providing quality education in various technical and non-technical domains.',
      website: 'https://spumandi.ac.in'
    },
    {
      name: 'Himachal Pradesh University',
      description: 'Renowned university offering UG and PG courses with strong research programs.',
      website: 'https://hpuniv.ac.in'
    },
    {
      name: 'IIT Mandi',
      description: 'An Institute of National Importance offering cutting-edge research and engineering programs.',
      website: 'https://iitmandi.ac.in'
    }
  ]);

  const [showForm, setShowForm] = useState(false);
  const [newUniversity, setNewUniversity] = useState({
    name: '',
    description: '',
    website: ''
  });

  const handleDelete = (index) => {
    const updated = universities.filter((_, i) => i !== index);
    setUniversities(updated);
  };

  const handleAdd = () => {
    if (newUniversity.name && newUniversity.description && newUniversity.website) {
      setUniversities([...universities, newUniversity]);
      setNewUniversity({ name: '', description: '', website: '' });
      setShowForm(false);
    } else {
      alert('Please fill in all fields.');
    }
  };

  return (
    <div className="p-6 bg-white rounded shadow">
      <h2 className="text-2xl font-bold mb-4">Manage Universities</h2>

      <button
        onClick={() => setShowForm(!showForm)}
        className="mb-4 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
      >
        {showForm ? 'Cancel' : 'Add University'}
      </button>

      {showForm && (
        <div className="mb-6 bg-gray-100 p-4 rounded shadow space-y-3">
          <input
            type="text"
            placeholder="University Name"
            value={newUniversity.name}
            onChange={(e) => setNewUniversity({ ...newUniversity, name: e.target.value })}
            className="w-full border px-3 py-2 rounded"
          />
          <textarea
            placeholder="Description"
            value={newUniversity.description}
            onChange={(e) => setNewUniversity({ ...newUniversity, description: e.target.value })}
            className="w-full border px-3 py-2 rounded"
          />
          <input
            type="url"
            placeholder="Website URL"
            value={newUniversity.website}
            onChange={(e) => setNewUniversity({ ...newUniversity, website: e.target.value })}
            className="w-full border px-3 py-2 rounded"
          />
          <button
            onClick={handleAdd}
            className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
          >
            Submit
          </button>
        </div>
      )}

      <div className="space-y-4">
        {universities.map((uni, index) => (
          <div key={index} className="border p-4 rounded shadow-sm">
            <h3 className="text-lg font-semibold text-blue-700">{uni.name}</h3>
            <p className="text-sm text-gray-600">{uni.description}</p>
            <a
              href={uni.website}
              target="_blank"
              rel="noopener noreferrer"
              className="text-blue-500 text-sm hover:underline"
            >
              Visit Website
            </a>
            <div className="mt-2 flex gap-2">
              <button
                onClick={() => handleDelete(index)}
                className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600 text-sm"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Universities;
