import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaTrash, FaUser, FaEnvelope, FaRupeeSign } from 'react-icons/fa';

const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE,
});

const Book = () => {
  const [books, setBooks] = useState([]);

  // Fetch books on component mount
  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      const response = await API.get('/books');
      setBooks(response.data);
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };

  const deleteBook = async (id) => {
    try {
      await API.delete(`/books/${id}`);
      setBooks(books.filter(book => book._id !== id));
    } catch (error) {
      console.error('Error deleting book:', error);
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Available Books</h2>
      {books.length === 0 ? (
        <p>No books available.</p>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          {books.map(book => (
            <div key={book._id} className="bg-white shadow rounded-lg p-4">
              <img
                src={`${process.env.REACT_APP_API_BASE}${book.image}`}
                alt={book.name}
                className="w-full h-40 object-cover rounded mb-3"
              />
              <h3 className="text-lg font-semibold">{book.name}</h3>
              <p className="flex items-center gap-2 text-sm text-gray-600">
                <FaUser /> {book.seller}
              </p>
              <p className="flex items-center gap-2 text-sm text-gray-600">
                <FaEnvelope /> {book.email}
              </p>
              <p className="flex items-center gap-2 text-sm text-green-600">
                <FaRupeeSign /> {book.price}
              </p>
              <button
                onClick={() => deleteBook(book._id)}
                className="mt-3 bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600 flex items-center gap-2"
              >
                <FaTrash /> Delete
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Book;
