// src/pages/CoursePages.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const CoursePages = () => {
  const { id } = useParams(); // University ID from the URL
  const [university, setUniversity] = useState(null);

  useEffect(() => {
    const fetchUniversity = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/universities/${id}`);
        setUniversity(res.data);
      } catch (err) {
        console.error('Failed to fetch university details:', err);
      }
    };

    fetchUniversity();
  }, [id]);

  if (!university) return <div className="text-center mt-10 text-gray-500">Loading...</div>;

  return (
    <div className="min-h-screen px-6 py-12 bg-gray-50 dark:bg-gray-900 text-gray-800 dark:text-white">
      <div className="max-w-5xl mx-auto text-center mb-10">
        <img src={university.logo} alt="Logo" className="h-20 mx-auto mb-4" />
        <h1 className="text-4xl font-bold text-blue-800 dark:text-blue-300">{university.name}</h1>
        <p className="text-gray-600 dark:text-gray-400">{university.description}</p>
        <p className="text-sm text-gray-500 mt-2">{university.location}</p>
      </div>

      <div className="max-w-4xl mx-auto">
        {university.departments.length === 0 ? (
          <p className="text-center text-gray-500">No departments found.</p>
        ) : (
          university.departments.map((dept, i) => (
            <div key={i} className="bg-white dark:bg-gray-800 rounded-lg shadow p-6 mb-6 border border-gray-200 dark:border-gray-700">
              <h2 className="text-2xl font-semibold text-blue-600 mb-4">{dept.name}</h2>
              {dept.courses.length === 0 ? (
                <p className="text-gray-500 text-sm">No courses available</p>
              ) : (
                <ul className="list-disc pl-6 space-y-2 text-left">
                  {dept.courses.map((course, j) => (
                    <li key={j} className="text-gray-700 dark:text-gray-300">
                      <strong>{course.name}</strong> ({course.level}) â€” {course.duration} years, {course.semesters} semesters
                    </li>
                  ))}
                </ul>
              )}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default CoursePages;
